--Arkham Encounter/Otherworld Encounter Color/Image URL\Arkham or Otherworld
function getLocationDetails(locationArray)
    location = locationArray[1]
    if locations_Details[location] ~= nil then
        return locations_Details[location] 
    end
    --log("NOT FOUND! " .. location)
    return ''
end